            panel3: { param: [, , ' Pulldown'], style: { preferredSize: [300, -1], margins: [11, 15, 10, 10], spacing: 2, orientation: 'column', alignChildren: ['left', ''] }, group1: { style: { spacing: 2 }, checkbox: { param: ['switchField', [0, 10, 15, 25]], style: { value: true } }, statictext: [, [0, 0, 100, 25], ''], dropdownlist: { param: ['fieldSeparationType', [0, 0, 150, 25], ['', '', '']], style: { alignment: ['fill', ''], selection: 0 } } }, group2: { style: { spacing: 2 }, checkbox: { param: ['switchPulldown', [0, 10, 15, 25]], style: { value: true } }, statictext: [, [0, 0, 100, 25], ' Pulldown'], dropdownlist: { param: ['removePulldown', [0, 0, 150, 25], ['', 'WSSWW', 'SSWWW', 'SWWWS', 'WWWSS', 'WWSSW', 'WWWSW - 24p ', 'WWSWW - 24p ', 'WSWWW - 24p ', 'SWWWW - 24p ', 'WWWWS - 24p ']], style: { alignment: ['fill', ''], enabled: false, selection: 0 } } } },
